mapper.select:
select 

,

from tb_health_care_sci_know
where itemID = #{itemid,jdbcType=INTEGER}
  and itemCode = #{itemcode,jdbcType=CHAR}
order by itemUpdateAt desc

mapper.delete:
delete from tb_health_care_sci_know
where itemID = #{key.itemid,jdbcType=INTEGER}
  and itemCode = #{key.itemcode,jdbcType=CHAR}

mapper.update:
update tb_health_care_sci_know


    SCIENCE_KNOWLEDGE_STATUS = #{scienceKnowledgeStatus,jdbcType=VARCHAR}


where itemID = #{key.itemid,jdbcType=INTEGER}
and itemCode = #{key.itemcode,jdbcType=CHAR}
