mapper.select:
select

from resource_role_ref
where itemID = #{itemid,jdbcType=INTEGER}
  and itemCode = #{itemcode,jdbcType=CHAR}
order by itemUpdateAt desc

mapper.delete:
delete from resource_role_ref
where itemID = #{itemid,jdbcType=INTEGER}
  and itemCode = #{itemcode,jdbcType=CHAR}

mapper.update:
update resource_role_ref
set RESOURCE_CODE = #{resourceCode,jdbcType=CHAR},
  ROLE_CODE = #{roleCode,jdbcType=CHAR},
  CREATER = #{creater,jdbcType=VARCHAR},
  itemCreateAt = #{itemcreateat,jdbcType=TIMESTAMP},
  UPDATER = #{updater,jdbcType=VARCHAR},
  itemUpdateAt = #{itemupdateat,jdbcType=TIMESTAMP}
where itemID = #{itemid,jdbcType=INTEGER}
  and itemCode = #{itemcode,jdbcType=CHAR}
